const gulp=require("gulp"),clean=require("gulp-clean"),htmlmin=require("gulp-htmlmin"),cleanCSS=require("gulp-clean-css"),minifyJs=require("gulp-minify");gulp.task("clean-app",()=>gulp.src("./dist/",{read:!0,allowEmpty:!0}).pipe(clean())),gulp.task("minify-html",()=>gulp.src("*.html").pipe(htmlmin({collapseWhitespace:!0})).pipe(gulp.dest("dist"))),gulp.task("minify-css",()=>gulp.src("*.css").pipe(cleanCSS({compatibility:"ie8"})).pipe(gulp.dest("dist"))),gulp.task("minify-js",function(){return gulp.src(["*.js"]).pipe(minifyJs({ext:{min:".js"},noSource:!0})).pipe(gulp.dest("dist"))}),gulp.task("default",gulp.series("clean-app","minify-html","minify-js","minify-css"));